import React, {useState, useEffect, createContext, useCallback} from 'react';

import UsersList from "../../components/UsersList/UsersList";
import {getUsers, updateUserProfile} from "../../containers/Users/hooks/crud";
import { useQuery, useMutation } from 'react-query';

export const UsersContext = createContext([]);
export const CurrentUserContext = createContext({});

function UsersListContainer() {
    const { data: response, isFetching } =useQuery('users', () => getUsers({id: 1}))
    console.log(response, isFetching);
    const users = response?.data || [];
    const createUser = useMutation(() => updateUserProfile);
console.log(createUser);
    const onSubmit = useCallback(
        async formData => {
            try {
                const {
                    data: { id },
                } = await createUser(formData);
                console.log(id);
            } catch (e) {
                // TODO: add errors handling if needs
            }
        },
        [createUser],
    );

    onSubmit({id:1});

    // const [users, setUsers] = useState([]);
    //
    // const getData = async () => {
    //     const {data} = await getUsers({id: 1});
    //     setUsers(data);
    // };
    //
    // useEffect(getData, []);
    //
    // const usersContext = {users, currentUser: {name: 'Vasya'}};

    return (
        <CurrentUserContext.Provider value={{name: 'Vasya'}} >
            <UsersContext.Provider value={users} >
                <UsersList />
            </UsersContext.Provider>
        </CurrentUserContext.Provider>
    );
}

export default UsersListContainer;
